import { useState } from "react";
import classes from "./Register.module.css"
import { verifyEmail, verifyPassword } from "../../utils/auth/verify";

export default function Register(props) {
    const [error, setError] = useState({
        isError: false,
        message: ""
    })
    const [registerInform, setRegisterInform] = useState({
        email:"",
        password:"",
        confirmpassword:"",
        firstname:"",
        lastname:""
    })

    const registerHandler = (e) =>{
        e.preventDefault();

    }

    const handleEventChange = (event) =>{
        const target = event.target;
        const value = target.value;
        const name = target.name;
        setRegisterInform({
            [name]:value,
        })
    }
    console.log(registerInform);

  return (
  <div className={classes.wrapper}>
    <h3 className={classes.title}>Register</h3>
      <form>
          <input onChange={handleEventChange} name="email" placeholder="Email"/>
          <input onChange={handleEventChange} name="password" placeholder="Password" type="password"/>
          <input onChange={handleEventChange} name="confirmpassword" placeholder="Confirm Password" type="password"/>
          <input onChange={handleEventChange} name="firstname" placeholder="First Name"/>
          <input onChange={handleEventChange} name="lastname" placeholder="Last Name"/>
          {error.isError&&<div className={classes.errorText}>{error.message}</div>}
        <button>Register</button>
      </form>
  </div>);
}
