import React, { Fragment, useEffect, useState } from "react";
import classes from "./MainPanel.module.css";
import HorizontalList from "../UI/List/HorizontalList";
import logoIcon from "../../assets/icons/Logo.svg";
import "react-slideshow-image/dist/styles.css";
import { Slide } from "react-slideshow-image";
import { useDispatch, useSelector } from "react-redux";
import CourseItem from "../Courses/CourseItem";
import { fetchHighlightCourses } from "../../store/slices/coursesSlice";

const MainPanel = () => {
  const dispatch = useDispatch();

  const listHighlightCourses = useSelector(
    (state) => state.courses.listMostViewCourses.entities
  );
  const listHighlightCoursesJSX = listHighlightCourses.map((element) => {
    console.log("avatar" ,element.avatar);
    return(
      <div key={element.course_id} className="each-slide">
        <div style={{ backgroundImage: `url("https://img-c.udemycdn.com/course/480x270/2021980_e27e_5.jpg?Expires=1621885562&Signature=BHIffw8NmIDfz-WuM2UdtbMK2hDHIWICZLcF4pGANM5DeIhuIm9Rl2apEfHbHPWfSRlJ629EK-XPajeAvLABp20NJRgVoajR2hfHaMk8uNbRv7VYr9g2hHsh5G6LXvBOnLuN9upv6taaGnMz56mz1PtSON1XyNu-u-hJc91b~tLbD1mRb9~1sdu3U9saZf0-tQ2Uh~-aTm0wtrAb6haXcCuvmHZ0ygf4APpsukHRF83yVQUGgRWGI3NMG6vkYHwzsHZJiRGnIJSmPB-4g4J4Zccox-tRV4e~iM1llWm6qQ0V0RdcMwm71C21td39vewZVFjErhD-QV4SnbBzw0AKmQ__&Key-Pair-Id=APKAITJV77WS5ZT7262A 2x")` }}>
          <span><CourseItem courseItem={element} /></span>
        </div>
      </div>
    )
  });

  useEffect(() => {
    dispatch(fetchHighlightCourses());
  }, []);

  return (
    <Fragment>
      <div className={classes.root}>
        <Slide easing="ease"> 
          {listHighlightCoursesJSX}
        </Slide>
      </div>
    </Fragment>
  );
};

export default MainPanel;
