import { useState } from "react";
import classes from "./Register.module.css";
import { verifyEmail, verifyPassword } from "../../utils/auth/verify";

export default function Register(props) {
  const [error, setError] = useState({
    isError: false,
    message: "",
  });
  const [registerInform, setRegisterInform] = useState({
    email: "",
    password: "",
    confirmpassword: "",
    firstname: "",
    lastname: "",
  });

  const registerHandler = (e) => {
    e.preventDefault();
    if (!verifyEmail(registerInform.email)) {
      setError({
        isError: true,
        message: "Invalid email",
      });
      return;
    }
    if (!verifyPassword(registerInform.password)) {
      setError({
        isError: true,
        message: "Password phải không có khoảng trắng và hơn 6 ký tự",
      });
      return;
    }
    if (registerInform.password !== registerInform.confirmpassword) {
      setError({
        isError: true,
        message: "Password not match",
      });
      return;
    }
    if(registerInform.firstname.trim().length===0 || registerInform.lastname.trim().length===0)
    {
        setError({
            isError: true,
            message: "Vui lòng điền đẩy đủ thông tin",
          });
          return;
    }
  };

  const handleEventChange = (event) => {
    setError({
      isError: false,
    });
    const target = event.target;
    const value = target.value;
    const name = target.name;
    setRegisterInform((prevState) => {
      return { ...prevState, [name]: value };
    });
  };
  console.log(registerInform);

  return (
    <div className={classes.wrapper}>
      <h3 className={classes.title}>Register</h3>
      <form onSubmit={registerHandler}>
        <input onChange={handleEventChange} name="email" placeholder="Email" />
        <input
          onChange={handleEventChange}
          name="password"
          placeholder="Password"
          type="password"
        />
        <input
          onChange={handleEventChange}
          name="confirmpassword"
          placeholder="Confirm Password"
          type="password"
        />
        <input
          onChange={handleEventChange}
          name="firstname"
          placeholder="First Name"
        />
        <input
          onChange={handleEventChange}
          name="lastname"
          placeholder="Last Name"
        />
        {error.isError && (
          <div className={classes.errorText}>{error.message}</div>
        )}
        <button onClick={registerHandler}>Register</button>
      </form>
    </div>
  );
}
