import React, { useEffect, useState } from "react";
import Dropdown from "react-multilevel-dropdown";
import "react-dropdown/style.css";
import { useDispatch, useSelector } from "react-redux";
import { fetchCategoriesList } from "../store/slices/categoriesSlice";

const CategoryDropdown = () => {
  const dispatch = useDispatch();
  const listCategory = useSelector(
    (state) => state.categories.listCategory.entities
  );
  console.log("List category: ", listCategory);

  const listCategoryJSX = listCategory.map((category) => {
    const listTopic = category.topics.map((topic) => {
      return <Dropdown.Item>{topic.title}</Dropdown.Item>
    });
    return (
      <Dropdown.Item onClick={() => {}}>
        {category.title}
        <Dropdown.Submenu position="right">
          {listTopic}
        </Dropdown.Submenu>
      </Dropdown.Item>
    );
  });
  useEffect(() => {
    dispatch(fetchCategoriesList());
  }, []);

  return (
    <Dropdown title="Category">
      {listCategoryJSX}
      <Dropdown.Item onClick={() => {}}>Item 1</Dropdown.Item>
      <Dropdown.Item>
        Item 2
        <Dropdown.Submenu position="right">
          <Dropdown.Item>Subitem 1</Dropdown.Item>
          <Dropdown.Item>Subitem 2</Dropdown.Item>
        </Dropdown.Submenu>
      </Dropdown.Item>
      <Dropdown.Item>Item 3</Dropdown.Item>
    </Dropdown>
  );
};
export default CategoryDropdown;
